<!DOCTYPE HTML PUBLIC "-//W3C//DTD HTML 4.01 Transitional//EN"><html>    <head>        <meta http-equiv="Content-Type" content="text/html; charset=UTF-8">        <title>LDAP Test 2</title>    </head>    <body>        <?php// parameters for ldap bind        $rdn = "fprofile-adpull@matrix.txstate.edu";        $pass = "7tq876gf";// connect to ldap server        $conn = ldap_connect('ldaps://matrix.txstate.edu', 636);        if ($conn) {            // bind to ldap server using parameters set above            $bind = ldap_bind($conn, $rdn, $pass);            // if bind succeeds begin ldap_search            if ($bind) {                // parameters for ldap search                $username = "os1023";                $dn = "OU=Txstate Users,DC=matrix,DC=txstate,DC=edu";                $filter = "(&(objectCategory=person)(sAMAccountName=$username))";                // additional a                $attributes = array("cn", "displayname", "mail", "title", "department", "telephonenumber");                // execute ldap search with the parameters set above                $search = ldap_search($conn, $dn, $filter, $attributes);                // if search succeeds retrieve the entries and store in entries array                if ($search) {                    // execute ldap get entries and store in entries array                    $entries = ldap_get_entries($conn, $search);                    // print out the particular entries                    // undefined indexes will result if the particular user does not have one of the fields below                    echo "cn: " . $entries[0]['cn'][0] . "<br />";                    echo "displayname: " . $entries[0]['displayname'][0] . "<br />";                    echo "mail: " . $entries[0]['mail'][0] . "<br />";                    echo "title: " . $entries[0]['title'][0] . "<br />";                    echo "department: " . $entries[0]['department'][0] . "<br />";                    echo "telephonenumber: " . $entries[0]['telephonenumber'][0] . "<br />";                } else {                    echo "LDAP search failed";                }            } else {                echo "LDAP bind failed...";            }        } else {            echo "LDAP conn failed..";        }        ?>    </body></html>